<!DOCTYPE html><html><head>    <meta charset="utf-8">    <meta http-equiv="X-UA-Compatible" content="IE=edge">    <meta name="viewport" content="width=device-width, initial-scale=1">    <title>Bootstrap Contact Form Template</title>    <link rel="stylesheet" type="text/css" href="<?php echo get_template_directory_uri(); ?>/css_uet/style_contact.css" />    <link rel="stylesheet" type="text/css" href="<?php echo get_template_directory_uri(); ?>/bootstrap_uet/css/bootstrap.min.css" /></head><style type="text/css"></style><?php/** * Template Name: contact page */get_header(); ?><body >    <div class="contact-body">            <div class="contact-container">            <div class="row">                <div class="col-sm-8 col-sm-offset-2 text">                    <h1 class="contact-form">Contact Form</h1>                     <br/>                </div>            </div>            <div class="row">                <div class="col-sm-6 col-sm-offset-3 form-box">                    <div class="contact-form-top">                        <div class="contact-form-top-left">                            <h3 class="contact-us">Contact us</h3>                            <p>Fill in the form below to send us a message</p>                        </div>                        <div class="contact-form-top-right">                            <i class="fa fa-envelope"></i>                        </div>                    </div>                     <div class="contact-form-bottom contact-form">                        <?php echo '<form action="' . esc_url( $_SERVER['REQUEST_URI'] ) . '" method="post">';?>                            <form role="form"  method="post">                                <div class="form-group">                                    <?php                                        echo '<input type="text" style= "font-size: 13px;" name="cf-name" placeholder="Name..." pattern="[a-zA-Z0-9 ]+" value="' . ( isset( $_POST["cf-name"] ) ? esc_attr( $_POST["cf-name"] ) : '' ) . '" size="80" />';                                    ?>                                </div>                                <div class="form-group">                                    <?php                                         echo '<input type="email" style= "font-size: 16px" placeholder="Email..." name="cf-email" value="' . ( isset( $_POST["cf-email"] ) ? esc_attr( $_POST["cf-email"] ) : '' ) . '" size="40" />';                                     ?>                                </div>                                <div class="form-group" id="form-control">                                    <?php                                         echo '<textarea rows="10" style= "font-size: 16px" cols="35" placeholder="Message..." name="cf-message">' . ( isset( $_POST["cf-message"] ) ? esc_attr( $_POST["cf-message"] ) : '' ) . '</textarea>';                                     ?>                                </div>                                <button type="submit" class="contact-btn">Send message</button>                            </form>                        <?php                             echo '</form>';                        ?>                        <?php                             if($_SERVER['REQUEST_METHOD'] == 'POST'){                                global $wpdb;                                $name = $_POST['cf-name'];                                $email = $_POST['cf-email'];                                $content = $_POST['cf-message'];                                $wpdb->insert(                                    'wp_contact',                                    array(                                        'name' => $name,                                        'email' => $email,                                        'content' => $content                                    ),                                    array(                                        '%s',                                        '%s',                                        '%s'                                    )                                );                            }                        ?>                    </div>                </div>            </div>        </div>        <br/>        <br/>    </div>       </body><?php     get_footer();     ?></html>